total_samples = len(data)
train_samples = int(total_samples * split_ratio)
test_samples = total_samples - train_samples


train_data = data[:train_samples]
test_data = data[train_samples:]

X = train_data[ : , : -1]
y = train_data[ : , -1]

test_X = test_data[ : , : -1]
test_y = test_data[ : , -1]

# padding the train data
ones = np.ones((X.shape[0] , 1))
X_padded = np.hstack((X , ones))
X_padded

def sigm( x , theta):
  z = np.dot( x , theta)
  ep = np.exp(-z)
  return ( (1) / ( 1 + ep))

# padding test-X
ones  = np.ones((test_X.shape[0] , 1))
test_X_padded = np.hstack((test_X ,ones))

theta = [0 , 0 ,0 ,0 ,0 ]

# gradient descent for the optimal parameters
alpha = 0.5
costs = []

iters = 1000


for i in range(iters):
  ptheta = theta
  for j in range(len(theta)):
    temp = 0;
    for xx, yy in zip(X_padded , y ) :
      fx = sigm(xx, ptheta)
      temp = temp + (( fx - yy ) * (xx[j]))
    temp /= (X_padded.shape[0])
    temp *= alpha
    theta[j] -= temp


  st = X_padded @ theta
  prob = ( (1) /( 1 + np.exp(-st)) )


  cost = 0
  for predd , yy in zip( prob , y ):
    if yy == 1:
      cost += -(np.log(predd))
    else:
      cost += -(np.log(1- predd))
  costs.append(cost)

plt.plot(costs)
plt.title("Cost vs #iteration-> ")
plt.xlabel("no of iterations")
plt.ylabel("Cost")
plt.show()

